import React, { useState } from 'react';
import './Tournament.css';
import PlayerRegistration from './PlayerRegistration';
import MatchDisplay from './MatchDisplay';
import NextMatch from './NextMatch';

function Tournament() {
  const [players, setPlayers] = useState([]); // To store player aliases
  const [currentMatch, setCurrentMatch] = useState(null); // To store the current match
  const [isTournamentStarted, setIsTournamentStarted] = useState(false);
  const [matchQueue, setMatchQueue] = useState([]); // Queue of upcoming matches

  // Function to add a new player
  const addPlayer = (alias) => {
    setPlayers([...players, alias]);
  };

  // Function to start the tournament and generate matches
  const startTournament = () => {
    if (players.length > 1) {
      setIsTournamentStarted(true);
      const generatedQueue = generateMatches(); // Generate the entire match queue
      setMatchQueue(generatedQueue);
      setCurrentMatch(generatedQueue[0]);
    } else {
      alert('At least two players are required to start the tournament.');
    }
  };

  // Function to generate the match queue
  const generateMatches = () => {
    const queue = [];
    for (let i = 0; i < players.length - 1; i++) {
      for (let j = i + 1; j < players.length; j++) {
        queue.push(`${players[i]} vs ${players[j]}`);
      }
    }
    return queue;
  };

  // Function to move to the next match
  const handleNextMatch = () => {
    const nextQueue = matchQueue.slice(1); // Remove the current match from the queue
    setMatchQueue(nextQueue);
    setCurrentMatch(nextQueue[0] || 'No more matches scheduled.');
  };

  return (
    <div className="tournament-container">
      <h1>Tournament</h1>
      {!isTournamentStarted ? (
        <>
          <PlayerRegistration addPlayer={addPlayer} players={players} />
          <button className="start-button" onClick={startTournament}>
            Start Tournament
          </button>
        </>
      ) : (
        <>
          <MatchDisplay match={currentMatch} />
          <NextMatch currentMatch={currentMatch} onNextMatch={handleNextMatch} />
          <div className="match-queue">
            <h3>Upcoming Matches</h3>
            {matchQueue.length > 1 ? (
              <ul>
                {matchQueue.slice(1).map((match, index) => (
                  <li key={index}>{match}</li>
                ))}
              </ul>
            ) : (
              <p>No more matches scheduled.</p>
            )}
          </div>
        </>
      )}
    </div>
  );
}

export default Tournament;
