import React, { useState } from 'react';
import './Tournament.css';
import PlayerRegistration from './PlayerRegistration';
import MatchDisplay from './MatchDisplay';
import NextMatch from './NextMatch';

function Tournament() {
  const [players, setPlayers] = useState([]); // Store player aliases
  const [currentMatch, setCurrentMatch] = useState(null); // Store the current match
  const [isTournamentStarted, setIsTournamentStarted] = useState(false);
  const [matchQueue, setMatchQueue] = useState([]); // Store the upcoming matches

  // Function to add a new player
  const addPlayer = (alias) => {
    setPlayers([...players, alias]);
  };

  // Function to start the tournament
  const startTournament = () => {
    if (players.length > 1) {
      setIsTournamentStarted(true);
      generateMatches();
    } else {
      alert('At least two players are required to start the tournament.');
    }
  };

  // Generate the matches (simple round-robin style)
  const generateMatches = () => {
    let queue = [];
    for (let i = 0; i < players.length; i++) {
      for (let j = i + 1; j < players.length; j++) {
        queue.push(`${players[i]} vs ${players[j]}`);
      }
    }
    setMatchQueue(queue);
    setCurrentMatch(queue.shift()); // Start with the first match
  };

  // Proceed to the next match
  const nextMatch = () => {
    if (matchQueue.length > 0) {
      setCurrentMatch(matchQueue.shift());
    } else {
      setCurrentMatch(null);
      alert('The tournament has ended!');
    }
  };

  return (
    <div className="tournament-container">
      <h1>Tournament</h1>
      {!isTournamentStarted ? (
        <>
          <PlayerRegistration addPlayer={addPlayer} players={players} />
          <button className="start-button" onClick={startTournament}>
            Start Tournament
          </button>
        </>
      ) : (
        <>
          <MatchDisplay match={currentMatch} />
          <NextMatch currentMatch={currentMatch} onNextMatch={nextMatch} />
        </>
      )}
    </div>
  );
}

export default Tournament;
