import React, { useState } from 'react';

function PlayerRegistration({ addPlayer, players }) {
  const [alias, setAlias] = useState('');

  const handleAddPlayer = () => {
    if (alias && !players.includes(alias)) {
      addPlayer(alias);
      setAlias('');
    } else {
      alert('Alias cannot be empty or already registered.');
    }
  };

  return (
    <div className="player-registration">
      <h2>Player Registration</h2>
      <input 
        type="text" 
        value={alias} 
        onChange={(e) => setAlias(e.target.value)} 
        placeholder="Enter alias" 
      />
      <button onClick={handleAddPlayer} className="submit-button">Add Player</button>
      <ul>
        {players.map((player, index) => (
          <li key={index}>{player}</li>
        ))}
      </ul>
    </div>
  );
}

export default PlayerRegistration;
